const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const formatMessage = require('format-message');

const axios = require('axios').default;

const blockIconURI = 'data:image/svg+xml;base64,';
const menuIconURI = 'data:image/svg+xml;base64,';

const apiURL = 'https://www.scpo.top:1120/v2';

class Libra {
    constructor(runtime) {
        this.runtime = runtime;
    }
    static get STATE_KEY() {
        return 'Libra.libra';
    }
    getInfo() {
        return {
            id: "libra",
            name: formatMessage({
                id: "libra.categoryName",
                default: "Libra",
                description: "Libra Redlist Plugin"
            }),
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [
            {
                opcode: 'isRedlistAvailable',
                blockType: BlockType.BOOLEAN,
                text: formatMessage({
                    id: 'libra.isRedlistAvailable',
                    default: 'is redlist available?',
                    description: 'redlist status'
                })
            },
            {
                opcode: 'isInList',
                text: formatMessage({
                    id: 'libra.isInList',
                    default: 'is [NAME] in the redlist?',
                    description: 'redlist check'
                }),
                blockType: BlockType.BOOLEAN,
                arguments: {
                    NAME: {
                        type: ArgumentType.STRING,
                        defaultValue: "Zerdot"
                    }
                }
            },
            {
                opcode: 'reason',
                text: formatMessage({
                    id: 'libra.inListReason',
                    default: 'reason of [NAME] being in redlist',
                    description: 'redlist reason'
                }),
                blockType: BlockType.REPORTER,
                arguments: {
                    NAME: {
                        type: ArgumentType.STRING,
                        defaultValue: "Zerdot"
                    }
                }
            }]
        }
    }
    
    isRedlistAvailable() {
		const data = {
			method: 'isInList',
			value: 'Zerdot',
			format: 'username',
			platform: 'aerfaying'
		};
        return new Promise(function (resolve, reject) {
            axios.post(apiURL, data).then(function (res) {
                resolve(res.data.status);
            });
        });
    }
    
    isInList(args) {
		const data = {
			method: 'isInList',
			value: args.NAME,
			format: 'username',
			platform: 'aerfaying'
		};
        return new Promise(function (resolve, reject) {
            axios.post(apiURL, data).then(function (res) {
                resolve(res.data.status);
            });
        });
    }
    
    reason(args){
		const data = {
			method: 'isInList',
			value: args.NAME,
			format: 'username',
			platform: 'aerfaying'
		};
        return new Promise(function (resolve, reject) {
            axios.post(apiURL, data).then(function (res) {
                resolve(res.data.status ? res.data.reason : 'The user isn\'t in the redlist');
            });
        });
    }
}
module.exports = Libra;
